import Vue from 'vue';
import VueRouter from 'vue-router';
import Home from '../views/Home.vue';
import Main from '../views/Main.vue';
import User from '../views/User.vue';
Vue.use(VueRouter);
const routes = [{
  path: '/',
  component: Main,
  children: [{
    path: 'home',
    component: Home
  }, {
    path: 'user',
    component: User
  }]
}];
const router = new VueRouter({
  routes // (缩写) 相当于 routes: routes
});

export default router;
